(()=>{const e=e=>{if(e.querySelector(".cc-btn"))return;const t=document.createElement("button");t.className="cc-btn",t.textContent="Copy",t.onclick=()=>{const n=e.querySelector("code");navigator.clipboard.writeText(n.innerText),t.textContent="Copied!",setTimeout(()=>t.textContent="Copy",1200)},e.appendChild(t)},t=e=>{if(e.querySelector(".cc-lang"))return;const s=e.querySelector("code"),n=(s.className.match(/language-(\w+)/)||[,])[1];if(!n)return;const t=document.createElement("span");t.className="cc-lang",t.textContent=n,e.appendChild(t)};document.querySelectorAll(".highlight").forEach(n=>{e(n),t(n)})})()